  position: relative;
  margin: 4px;
}
.quickSelect .quickSelectItemCont.disabled .quickSelectItem {
  background: gray !important;
}
.quickSelect .quickSelectItemCont .quickSelectItem {
  width: 25px;
  height: 25px;
  border-radius: 25px;
  user-select: none;
  cursor: pointer;
  display: block;
  position: relative;
  text-align: center;
  line-height: 25px;
  transition: 0.3s;
}
.quickSelect .quickSelectItemCont .quickSelectItem .commentIcon {
  opacity: 0;
  transition: 0.4s;
  position: absolute;
  top: -5px;
  right: -5px;
  width: 20px;
  height: 20px;
  color: black;
}
.quickSelect .quickSelectItemCont .quickSelectItem .commentIcon svg {
  position: absolute;
  left: 0;
}
.quickSelect .quickSelectItemCont .quickSelectItem:hover {
  transform: scale(1.1);
  opacity: 0.8;
}
.quickSelect .quickSelectItemCont .quickSelectItem:hover .commentIcon {
  opacity: 0.7;
}
.quickSelect .quickSelectItemCont .quickSelectItem.green {
  background: #5cce89;
  color: black;
}
.quickSelect .quickSelectItemCont .quickSelectItem.green:active {
  background: #42a76a;
}
.quickSelect .quickSelectItemCont .quickSelectItem.red {
  background: #ff6961;
  color: white;
}
.quickSelect .quickSelectItemCont .quickSelectItem.red:active {
  background: #e74f47;
}
.quickSelect .quickSelectItemCont .quickSelectItem.cannot {
  user-select: none;
  pointer-events: none;
  cursor: default;
  background: gray;
  color: black;
}
