.v-popover > div {
  display: block !important;
}

.tooltip.popover {
  outline: 0 !important;
}
.tooltip.popover .classPopper .popover-inner {
  background: #e3ebff;
}
.tooltip.popover .classPopper .popover-arrow {
  border-color: #e3ebff;
}
.tooltip.popover .classPopper .tooltip-inner {
  max-width: unset !important;
}

@media only screen and (max-width: 850px) {
  .redclass-item {
    display: flex !important;
    justify-content: center;
  }
}
.commentIcon {
  position: absolute;
  bottom: 5px;
  right: 5px;
  width: 20px;
  height: 20px;
  color: #32326b;
}
.commentIcon svg {
  position: absolute;
  left: 0;
}

.classescont {
  width: 100%;
  height: calc(100vh - 152px);
  overflow-y: auto;
  overflow-x: hidden;
  padding-right: 5px;
  padding-left: 5px;
}

@media screen and (max-width: 768px) {
  .classescont {
    height: auto;
    border-radius: 10px;
    background: #e3ebff;
    padding: 25px;
  }
}
.commentBody {
  width: fit-content;
  height: fit-content;
  padding: 4px 6px;
  font-size: 20px;
  color: #32326b;
  font-family: Fanta, sans-serif;
}
.commentBody pre {
  white-space: pre-wrap;
  padding: 0;
  margin: 0;
  line-height: 20px;
}

.redclass-item {
  border-radius: 10px;
  height: 100px;
  padding: 10px;
  display: block;
  position: relative;
  background: #e3ebff;
  margin-bottom: 40px;
}
.redclass-item:hover .commentPopper .commentBody {
  transform: translateY(7px);
  opacity: 1;
}
.redclass-item .title {
  height: 35px;
}
.redclass-item .title text {
  font-family: mr-eaves-modern, sans-serif;
  font-weight: 200;
  font-size: 17px;
}
.redclass-item .subtext {
  text-align: center;
  font-size: 21px;
  color: white;
  width: 65px;
  position: absolute;
  bottom: 10px;
  background: #ff6961;
  font-family: futura-pt, sans-serif;
  font-weight: 400;
  font-style: normal;
  line-height: 29px;
  padding-bottom: 0px;
  font-size: 18px;
  border-radius: 12px;
  display: block;
}
.redclass-item:nth-last-child(1) {
  margin-bottom: 25px;
}
.redclass-item.greenclass {
  border: 0;
}
.redclass-item.greenclass .subtext {
  width: 90px;
  background: #5cce89;
  color: #32326b;
}
.redclass-item.greenclass .title {
  color: #32326b;
}
