#teacher {
  height: 100% !important;
  margin: 15px 0;
  overflow-x: auto;
}

.useritemcol {
  height: 90px;
}

.editmsg {
  font-size: 9px;
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.fa-comment {
  width: 18px;
  height: 18px;
}

.useritemspan {
  padding: 0px;
  border-radius: 10px;
  background: white;
  display: block;
  font-family: Roboto;
  z-index: 1;
}

.commentPopper .popper {
  z-index: 3;
}

@keyframes bounceIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@keyframes bounceOut {
  from {
    opacity: 1;
  }
  to {
    opacity: 0;
  }
}
.bounceIn .popper {
  animation: 200ms ease-in forwards bounceIn;
  animation-duration: 200ms;
  animation-fill-mode: forwards;
}

.bounceOut .popper {
  animation: 200ms ease-out forwards bounceOut;
  animation-duration: 200ms;
  animation-fill-mode: forwards;
}

.fade-enter-active,
.fade-leave-active {
  transition: opacity 2.5s;
}

.fade-enter,
.fade-leave-to {
  opacity: 0;
}

.useritem {
  height: fit-content;
  padding: 6px;
  height: 63px;
  font-family: Roboto;
  cursor: pointer;
}
.useritem .name {
  width: 60%;
  float: left;
  top: 3px;
  line-height: 18px;
  position: relative;
  margin: 5px;
}
.useritem .btn {
  float: right;
  right: 15px;
  top: 50%;
  position: absolute;
  transform: translate(0%, -50%);
}
.useritem .btn.red {
  background: #ff6961;
  color: white;
}
.useritem .btn.green {
  background: #5cce89;
  color: black;
  padding-left: 5px;
  padding-right: 5px;
}

@keyframes visi {
  0% {
    display: none;
  }
  59% {
    display: none;
  }
  60% {
    display: block;
  }
  100% {
    display: block;
  }
}
span > .popper {
  width: 221px;
  transition: opacity 0.2s;
  font-family: Roboto;
  z-index: 2;
}
span > .popper .popperBlock {
  width: 45px;
  height: 45px;
  border-radius: 10px;
  user-select: none;
  margin: 4px;
  cursor: pointer;
  float: left;
  text-align: center;
  display: block;
  position: relative;
  line-height: 45px;
  transition: 0.3s;
  z-index: 2;
}
span > .popper .popperBlock .commentIcon {
  opacity: 0;
  transition: 0.4s;
  position: absolute;
  top: -5px;
  right: -5px;
  width: 20px;
  height: 20px;
  color: black;
  z-index: 2;
}
span > .popper .popperBlock .commentIcon svg {
  position: absolute;
  left: 0;
}
span > .popper .popperBlock:hover {
  transform: scale(1.07);
  box-shadow: 0 1px 5px rgba(0, 0, 0, 0.15);
}
span > .popper .popperBlock:hover .commentIcon {
  opacity: 0.7;
  animation: visi 2s forwards;
}
span > .popper .popperBlock.green {
  background: #5cce89;
  color: black;
}
span > .popper .popperBlock.green:active {
  background: #42a76a;
}
span > .popper .popperBlock.red {
  background: #ff6961;
  color: white;
}
span > .popper .popperBlock.red:active {
  background: #e74f47;
}
span > .popper .popperBlock.cannot {
  user-select: none;
  pointer-events: none;
  cursor: default;
  background: gray;
  color: black;
}
